package day12;

public class OrLogicalWithSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	char grade = 'B';
	// A,B,C -> pass, D,E-> faild
switch (grade) {
case 'A':
	System.out.println("pass");
break;
case 'B':
	System.out.println("pass");
break;
case 'C':
	System.out.println("pass");
break;
case 'D':
	System.out.println("failed");
break;
case 'E':
	System.out.println("failed");
break;
default : 
	System.out.println("Invalid grade");

}




	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
}
}