package day20;

public class ForLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=10;i++) {
			System.out.println("I love Java");
		}
		
		System.out.println();
		for(int j=5;j<=10;j++) {
			System.out.print(j+ ", ");
		}
		for(int m=20; m>=10; m--) {
			System.out.println(m);
		}
		
		
	}

}
