package day4_primitives_operators;

public class Primitives {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 byte byteValue = 127;
 short shortValue = 2300;
 int intValue = 123454;
 long longValue = 123455678999L;
 float floatValue = 12.33F;
 double doubleValue = 123.566;
 char charValue= '@';
 char charValue2 = 12345;
 boolean booleanValue= true;
 boolean booleanValue2= false;
 System.out.println("byte "+ byteValue );
 System.out.println("short "+ shortValue);
 System.out.println("int "+ intValue);
 System.out.println("long "+ longValue);
 System.out.println("float "+ floatValue);
 System.out.println("double "+ doubleValue);
 System.out.println("char "+ charValue);
 System.out.println("char "+ charValue2);
 System.out.println("boolean "+ booleanValue);
 System.out.println("boolean "+ booleanValue2);
 
 
 
 
 
	
	
	
	}
	

}
